Create table all_sequences (
	sequence_name varchar2(100),
	sequence_owner varchar2(100)
);


commit;

Create table all_tables (OWNER                     VARCHAR2(30) NOT NULL,
                         TABLE_NAME                VARCHAR2(30) NOT NULL,
                         TABLESPACE_NAME           VARCHAR2(30),
                         INITIAL_EXTENT            NUMBER,
                         TEMPORARY                 VARCHAR2(1),
                         NESTED                    VARCHAR2(3),
                         DURATION                  VARCHAR2(15)
);

commit;

Create table all_tab_columns (OWNER                VARCHAR2(30) NOT NULL,
                              TABLE_NAME           VARCHAR2(30) NOT NULL,
                              COLUMN_NAME          VARCHAR2(30) NOT NULL,
                              DATA_TYPE            VARCHAR2(106),
                              DATA_TYPE_MOD        VARCHAR2(3),
                              DATA_TYPE_OWNER      VARCHAR2(120),
                              DATA_LENGTH          NUMBER NOT NULL,
                              DATA_PRECISION       NUMBER,
                              DATA_SCALE           NUMBER,
                              NULLABLE             VARCHAR2(1),
                              COLUMN_ID            NUMBER,
                              DEFAULT_LENGTH       NUMBER,
                              DATA_DEFAULT         LONG
);

commit;

Create table all_col_comments (OWNER       VARCHAR2(30) NOT NULL,
                               TABLE_NAME  VARCHAR2(30) NOT NULL,
                               COLUMN_NAME VARCHAR2(30) NOT NULL,
                               COMMENTS    VARCHAR2(4000)
);

Create table all_tab_comments  (OWNER      VARCHAR2(30) NOT NULL,
                                TABLE_NAME VARCHAR2(30) NOT NULL ,
                                TABLE_TYPE VARCHAR2(11),
                                COMMENTS   VARCHAR2(4000)
);

Create table ALL_mviews (OWNER                  VARCHAR2(30) NOT NULL,
                         MVIEW_NAME             VARCHAR2(30) NOT NULL
);

